#include <iostream>#include <iomanip>using namespace std;int main(){	const int n = 10, m = 20;	int a[n][m];	int i, j;	int sign = 1;	//Введення масиву a	//cout << "Input an array A\n";	srand(time(NULL));	for (i = 0; i < n; i++)	{		for (j = 0; j < m; j++)		{			a[i][j] = rand() % 100;			cout << "a[" << i << "][" << j << "]=" << a[i][j] << " " << endl;		}	}	//Виведення масиву А у вигляді матриці	cout << "\n\nArray a\n\n";	for (i = 0; i < n; i++)	{		for (j = 0; j < m; j++)		{			cout << setw(3) << a[i][j];		}		cout << "\n";	}	//Виведення масиву А у вигляді матриці	cout << "\n\nResult array a\n\n";	bool isInArea1 = false;	bool isInArea2 = false;	for (i = 0; i < n; i++)	{		for (j = 0; j < m; j++)		{			isInArea1 = (2 <= i && i <= 5) && (3 <= j && j <= 4);			isInArea2 = (7 <= i && i < n) && (7 <= j && j < m);			if (isInArea1 || isInArea2)			{				cout << setw(3) << a[i][j];			}			else			{				cout << setw(3) << "**";			}		}		cout << "\n";	}}
