#include <iostream>#include <string>#define STOP "*"using namespace std;struct STUDENT{	int num;	string name;	int mark;};int main(){	static STUDENT s[25];	int i = 0;	string name;	cout << "Please input array of students struct\n";	do	{		cout << "Name:" << endl;		cin >> name;		if (name != STOP)		{			s[i].name = name;			cout << "Number:" << endl;			cin >> s[i].num;			cout << "Mark:" << endl;			cin >> s[i].mark;			i++;		}	} while (name != STOP);	int n = i;	int j = 0;	while (j < n)	{		cout << s[j].num << " " << s[j].name << " " << s[j].mark << endl;		j++;	}	getchar();	return 0;}
